@use 'abstracts/' as *;


.nav-bar {
    width: 100%;
    height: 60px;
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    z-index: 999;
    color: $white;
    background-color: $black;
    padding: 0 40px;
    display: flex;
    justify-content: space-between;
    align-content: center;
    align-items: center;
    user-select: none;
    a {
        text-decoration: none;
    }

    .logo {
        color: $red;
        text-transform: uppercase;
        font-size: $heading;
        cursor: pointer;
        transition: all .3s;
        
        &:hover {
            -webkit-text-stroke: 1px $red;
            color: transparent;

        }
    }

    .hamburger {
        display: none;
    }

    .nav-lins.active {
        display: block;
    }

    .active {
        display: block;
    }

    .nav-links {

        

        a {
            display: inline-block;
            text-decoration: none;
            color: $white;
            text-transform: uppercase;
            font-weight: $normal;
            font-size: $regular;
            margin-left: 3rem;
            position: relative;


            &::before {
                content: '';
                position: absolute;
                width: 100%;
                height: 2px;
                bottom: 0;
                background-color: $red;
                transform: scaleX(0);
                transform-origin: right;
                transition: transform .5s;

            }

            &:hover::before {
                transform-origin: left;
                transform: scaleX(1);
            }

        }

        
    }

    
}

