            display: inline;
            font-size: $regular;
        }

        .envelop-icon {
            font-size: $regular2;
            margin-top: 1em;
            margin-right: .5em;

        }

        &__email {
            display: inline;
            font-size: $regular;
        }

        &__icons {
            font-size: $heading;
            margin-top: 1em;
            display: flex;

            a {
                color: $white;
                margin-right: .8em;
                transition: all .5s;
                
                &:hover {
                    color: $red;
                    transform: translateY(-.3em);
                }
            }
        }
    }

    .form-div {
        width: 50%;
        height: 100%;
        form {
            display: flex;
            flex-direction: column;
            width: 70%;
            justify-content: center;
            margin-left: auto;
            position: relative;

            input {
                &[type="email"], &[type="text"] {
                    width: 100%;
                    height: 2.5em;
                    padding-left: .8em;
                    background-color: transparent;
                    color: $white;
                    border: 0.125em solid $white;
                    
                    margin-bottom: 1em;
                    font-size: $regular

                    
                }

                &:focus {
                    outline: none;
                    border: 0.1875 solid $red;
                }
                
                &:-webkit-autofill {
                    box-shadow: 0 0 0px 1000px $black inset;
                    -webkit-text-fill-color: $white;
                }
                
            }

            #message-box {
                background-color: transparent;
                color: $white;
                font-size: $regular;
                border: 0.125em solid $white;
                
                margin-bottom: 1em;
                padding-left: .8em;
                padding-top: .2em;
                resize: none;
                width: 100%;


                &:focus {
                    border: 0.1875 solid $red;
                    outline: none;
                }
            }

            .submit {
                @include button-size;
                background-color: transparent;
                border: 2px solid $white;

                span {
                    @include button-text;
                    color: $white;
                    transition: all .5s;
                }

                &::before {
                    content: '';
                    position: absolute;
                    width: 140%;
                    height: 100%;
                    top: 0;
                    left: -20%;
                    background-color: $white;

                    transform: skewX(40deg) scaleX(0);
                    transform-origin: left;
    
                    transition: all .5s;
                }
                
                &:hover::before {
                    transform: skewX(40deg) scaleX(1);
                }
                &:hover {
                    border: none;
                }
    
                &:hover span {
                    color: $black;
                }
            }
        }
    }
}

