        &__image {
            width: 50%;
            display: flex;
            justify-content: center;
            align-items: center;
            img {
                width: 30em;
                user-select: none;
            }
        }

        &__text {
            width: 50%;
            padding-left: 2em;
            padding-right: 2em;
            padding-top: 1.5em;

            &_heading {
                font-size: $big-heading;
                line-height: 3em;
                font-weight: 800;
                letter-spacing: -.07em;
                margin-bottom: 3em;
            }

            &_list{
                text-transform: uppercase;
                padding-left: 2em;
                font-size: $regular2;
                margin-bottom: 2em;
            }

            &_description {
                font-size: $regular;
                width: 95%;
                margin-bottom: 3em;
            }

            &_price {
                font-size: $big-heading;
                font-weight: 800;
                letter-spacing: -.04em;

            }

            .button-div {
                @include button-size;
                width: 22em;
                height: 3.5em;
                background-color: $red;
                margin-top: 2.5em;

                transition: all .3s;

                a {
                    @include button-text;
                    font-size: $button-font;
                    color: $black;
                    font-weight: 800;
                    text-transform: uppercase;

                    transition: all .3s;


                    &:hover {
                        color: $red;
                    }
                }

                &:hover{
                    background-color: $black;
                    border: 2px solid $red;
                }

                
            }

            .red {
                background-color: $red;


                a {

                    &:hover {
                        color: $red;
                    }
                }


                &:hover{
                    background-color: transparent;
                    border: 2px solid $red;
                }
            }
        }
    }

    &__note {
        text-align: center;
        margin-top: 7em;
        font-size: $regular2;
        font-weight: 100;
    }


}

