@use 'abstracts/' as *;


.nav-bar {
    width: 100%;
    height: 60px;
    position: fixed;
    z-index: 999;
    color: $white;
    background-color: $black;
    padding: 5px 40px;
    display: flex;
    justify-content: space-between;
    align-content: center;
    align-items: center;

    .logo {
        color: $red;
        text-transform: uppercase;
    }

    .nav-links {
        display: flex;
        list-style: none;
        justify-content: space-between;
        width: 25%;

        li a {
            text-decoration: none;
            color: $white;
            text-transform: uppercase;
            font-weight: $bold;
            font-size: $regular;

        }
    }
}
